	const [localShowTab, setLocalShowTab] = useState ('0')

	const [isShown, setShown] = useState (false)
	const [fetchError, setFetchError] = useState ('')

	const [tabElement, setTabElement] = useState (<Tab0 setShown = {setShown} setFetchError = {setFetchError}/>)



	//console.log ('local: ', localShowTab, 'app: ', props.showTab)
	// SWITCH HANDLER
	if (localShowTab !== props.showTab) {
	//	switchHandler()
	}

	useEffect (switchHandler, [props.showTab])

	const sleep = (milliseconds) => {
  return new Promise(resolve => setTimeout(resolve, milliseconds))
}

	async function switchHandler () {



		console.log ('change')
		setShown (false)
		await sleep (5000)
		changeTab ()
		await sleep (1000)
		setShown (true)

		function changeTab () {
			if (props.showTab === '0') setTabElement ( <Tab0 setShown = {setShown} setFetchError = {setFetchError}/> )
			if (props.showTab === '1') setTabElement (<Tab1 /> )
			//if (props.showTab === '2') tabElement = <Tab2 />
			//if (prosp.showTab === '3') tabElement = <Tab3 />
		}
	}

	// vars
	let mainTrans = '-100%'
	if (isShown) mainTrans = '0%'

	return (
		<div
			style = {{height: '79.61vh', position: 'fixed', top: '12.39vh', width: '100%'}}
		>	
			<div 
           	 	style = {{height: '100%', transform: `translateX(${mainTrans})`, transition: 'transform 0.5s ease-out', display: 'flex'}}
        	>	

        			{tabElement}				

				{fetchError !== '' && '<h5>{fetchError}</h5>'}
			</div>
		</div>
	)
}

export default Middle
