import React, {useState, useEffect} from 'react'

const Global = (prop) => {	


	//BACK END STUFF
	useEffect (() => backEnd(), [])

	const backEnd = () => {
		const requestOptions = {
			method: 'GET',
			redirect: 'follow'
		}
		fetch('https://api.covid19api.com/summary', requestOptions)
			.then (response => response.json())
			.then (result => {
				resulting (result)
			})
			.catch(error => console.error (error))
	}

	const resulting = (data) => {
		// calculating tops
		// calc top 3
		let numArray = []
		data.Countries.map (item => numArray.push (item.TotalConfirmed))
		console.log (numArray)
	}

	//VARS
	let mainTrans = '-100'
	if (prop.isOpen) mainTrans = '0'

	return (
		<div style = {{ transform: `translateY(${mainTrans}%)`, transition: 'transform 0.4s ease',
			height:'30vh', width: '100%', backgroundColor:'purple'}}>

		</div>
	)
}

export default Global
