import React, { useState, useEffect } from 'react'

const Tab0 = (props) => {

    const [isLoaded, setLoaded] = useState (false)
    const [localData, setLocalData] = useState ([false, []])
    const [updateTime, setUpdateTime] = useState ()

    const [updatePSize, setUpdatePSize] = useState ()
    const [zabProps, setZabProps] = useState ([])

    useEffect (() => {
        fetchLocalData ()
        calcSizes ()
    }, [])

    // FETCH DATA
    const fetchLocalData = () => {
        fetch ('https://api.apify.com/v2/key-value-stores/1brJ0NLbQaJKPTWMO/records/LATEST?disableRedirect=true')
        .then (response => response.json())
        .then (result => {
            props.setShown (true)
            setLoaded (true)
            setLocalData ([true, result.infectedByRegion[50]])
            setUpdateTime (result.lastUpdatedAtSource.toString().slice (0, -14))
        })
        .catch (error => {
            setLoaded (false)
            props.setFetchError (error)
            console.error (error)
        })
    }

    // CALC SIZES
    const calcSizes = () => {
        //calc update time
        const cUpPSize = (document.querySelector('.updateDiv').clientHeight / 2.225)
        setUpdatePSize (cUpPSize+'px')
        // calc zab title
        const zabTitleHeight = (document.querySelector('.zabTitle').clientHeight)
        const zabDiv = document.querySelector('.zabDiv')
        const zabTop = (zabTitleHeight/2) - (zabDiv.clientHeight/2)
        const zabFont = (zabTitleHeight/3)
        const zabWidth = zabDiv.clientWidth+'px'
        setZabProps ([zabTop+'px', zabFont+'px', zabTitleHeight*0.7+'px', zabWidth])
    }

    // VAR ELEMENTS
    let updateDownload = null
    if (localData[0] === true) {
        let date = new Date (updateTime)
        let day =  date.getDate ()
            if (day < 10) day = '0' + day
        let month = date.getMonth () + 1
            if (month < 10) month = '0' + month
        updateDownload = `${day}.${month}.${date.getFullYear()}`
    }

    return (
        <div style = {{height: '100%'}}>
            <div style = {{height: '95%', borderRadius: '30px', width: '95%', margin: 'auto ', background: 'linear-gradient(143deg, #ff6969 6%, #cd3d3d 50%, #a81c1c 93%)',
                    boxShadow: '3px 3px 10px 0 rgba(0, 0, 0, 0.2), -5px -5px 10px 0 rgba(255, 255, 255, 0.8)'}}
            >

                <div className = 'zabTitle' style = {{height: '12%'}} >
                    <div className = 'zabDiv' style = {{height: '70%',position: 'relative', top: zabProps[0], backgroundColor: '#ffffff', marginLeft: '8%', padding: '0 5%', display: 'inline-block'}}>
                        <p style = {{fontSize: zabProps[1], margin: 0, lineHeight: zabProps[2]}}>В Забайкалье :</p>
                    </div>
                </div>
                <div style = {{height: '49%', backgroundColor: 'white'}} ></div>
                <div style = {{height: '39%', backgroundColor: 'black'}} ></div>

            </div>
            <div className = 'updateDiv' style = {{height: '5%', display: 'flex', justifyContent: 'end', margin: '1% 10%'}}>
                <div style = {{flex: 1}} />
                <p style = {{fontSize: updatePSize ,margin: '0', fontWeight: '300'}} >Обновлено {updateDownload}</p>
            </div>
        </div>
    )
}

export default Tab0
