import React, {useState, useEffect} from 'react'

const Tab1 = (props) => {

	const [headerProps, setHeaderProps] = useState ([])

	//CALC SIZES
	useEffect (() => calcSizes (), [])

	const calcSizes = () => {
		//calc header
		const headerHeight = document.querySelector('.headerDiv').clientHeight
			//font size
			const headerFontSize = headerHeight / 4.3
			console.log (headerHeight)

		setHeaderProps ([headerHeight,headerFontSize])
	}

	let mainDisplay = 'none'
	if (props.tab === '1') mainDisplay = 'block'

	//STYELS
	const liS = {
		backgroundColor: '#d8d8d8'
	}
	const liDivS = {
		height: "90%",
		width: '90%',
		margin: 'auto'
	}

	return (
		<div
			style = {{
				position: 'relative', display: mainDisplay,
				height: '97%', width: '95%', margin: '0 auto', backgroundColor: '#e5e5e5',
				borderRadius: '33px', boxShadow: '-7px -7px 24px rgba(255,255,255,1), 7px 7px 24px rgba(0,0,0,0.2)',
				overflow: 'hidden'
		}}
		>
		 	<div
		 		className = 'headerDiv'
		 		style = {{height: '11.39vh', width: '100%', backgroundColor: '#f46060', borderRadius: '33px'}}
		 	>
		 		<p style = {{fontSize: headerProps[1], lineHeight:headerProps[0]/2.5+'px', paddingTop: '2%'}}>
		 			Рекомендации по проилактике Covid19
		 		</p>
		 	</div>

		 	<div style = {{width: '88%', height: '100%', margin: '0 auto'}} > 

		 		<ul style = {{listStyle: 'none'}}>
		 			<li style = {liS}>
		 				<div style = {liDivS}>
		 					<div>

		 					</div>

		 				</div>
		 			</li>
		 		</ul>

		 	</div>
		</div> 
	)
}

export default Tab1
