import React, { useEffect, useState } from 'react'

import {ReactComponent as Home} from '../icons/home.svg'
import {ReactComponent as Book} from '../icons/book.svg'
import {ReactComponent as MapI} from '../icons/map.svg'
import {ReactComponent as Stat} from '../icons/stat.svg'

const Bottom = (props) => {

	const [iconProps, setIconProps] = useState ([])

	//handle size
	useEffect (() => {
		handleSize ()
	}, [])

	const handleSize = () => {
		const bottomDiv = document.querySelector('.bottomDiv')
		const bottomHeight = bottomDiv.clientHeight
		const bottomWidth = bottomDiv.clientWidth/4
		let prioritySize = bottomHeight
		if (bottomWidth < bottomHeight) prioritySize = bottomWidth
		// calc size
		const iconSize = prioritySize/8 * 3
		// calc margin
		const iconMargin = (bottomHeight/2) - (iconSize/2)
		setIconProps([iconSize, iconMargin]) 
		console.log (iconSize)

	}

	// styles
	const iconStyle = {height: iconProps[0], marginTop: iconProps[1]}

	const lineStyle = {height: '65%', width: '2px', backgroundColor: '#000000', borderRadius: '100px'}


	return (
		<div style = {{height: '10vh', position: 'fixed', top: '90vh', width: '90%', marginLeft: '5%'}}>
			<div
				className = 'bottomDiv'
				style = {{display: 'flex', justifyContent: 'space-around', height: "100%", width: '100%', backgroundColor: '#e0e0e0', borderRadius: '20px'}}
			>
				<Home style = {iconStyle} />

				<div style = {lineStyle} />

				<Book style = {iconStyle} />

				<div style = {lineStyle} />

				<MapI style = {iconStyle} />

				<div style = {lineStyle} />

				<Stat style = {iconStyle} />

			</div>
		</div>
	)
}

export default Bottom
