import React, { useState, useEffect } from 'react'

const Header = () => {

	const [logoFontSize, setLogoFontSize] = useState ()
	const [logoTopMargin, setLogoTopMargin] = useState ()

	// HANDLE SIZES
	useEffect (() => {
		handleSize ()
	}, [])

	const handleSize = () => {
		const headHeight = document.querySelector('header').clientHeight
		// calc logoFSize
		const cLogoFontSize = headHeight / 2.166
		setLogoFontSize (cLogoFontSize+'px')
		// calc logoTopMargin
		const cLogoTopMargin = (headHeight/2) - (((headHeight/100)*40)/2)
		setLogoTopMargin (cLogoTopMargin)
		console.log (cLogoTopMargin, document.querySelector('.logoH1').clientHeight)
	}

	return (
		<header
			style = {{height: '8.135vh', width: '81.86%', margin: '0 auto', top: '2.50vh', backgroundColor: '#e0e0e0', position: 'relative',
				boxShadow: '3px 3px 10px 0 rgba(0, 0, 0, 0.2), -3px -3px 10px 0 rgba(255, 255, 255, 0.8)', borderRadius: '20px'
			}}
		>
			<div>
				<div
					style  = {{height: '4vh', margin: 'auto 0'}}
				>
					<h1 className = 'logoH1' style = {{position: 'fixed', fontFamily: 'Poppins, Roboto', fontWeight: 'regular', padding: 0, margin: 0, fontSize: logoFontSize}}>Covid75</h1>
				</div>
				<span> </span>
			</div>
		</header>
	)
}

export default Header
