import React , {useState}from 'react';

import Header from './com/header/Header'
import Middle from './com/middleStuff/Middle'
import Bottom from './com/bottom/Bottom'

function App() {
  const {infoOpen, setInfoOpen} = useState (false)

  const [isVirgin, setVirginity] = useState (true)
  const [showTab, setShowTab] = useState ('0')


  //HANDLERS
  function setTab (value) {
    if (isVirgin) (setVirginity (false))
    setShowTab (value)
  }

  function handleInfo () {
    setInfoOpen (prevInfoOpen => !prevInfoOpen)
  }

  return (
    <div className="App"
    >
      <div>
        <Header 
          infoOpen = {infoOpen}
          handleInfo = {handleInfo}
        />

        <Middle
          showTab = {showTab}
          isVirgin = {isVirgin}
        />

        <Bottom 
          setTab = {setTab}
          showTab = {showTab}
        />
      </div>
    </div>
  );
}

export default App;
