import React, {useEffect, useState} from 'react';

function App() {
  const [localData, setLocalData] = useState ([])  

  // FETCH DATA

  useEffect (() => {
    fetchBigData ()
  },[])

  function fetchBigData () {
    fetch ('https://api.apify.com/v2/key-value-stores/1brJ0NLbQaJKPTWMO/records/LATEST?disableRedirect=true')
    .then (response => response.json())
    .then (result => {
      console.log (result.infectedByRegion[50])
      setLocalData (result.infectedByRegion[50])
    })
    .catch (error => {
      console.error (error)
    })
  } 

  return (
    <div className="App">
      <header
        style = {{
          height: '10vh',margin: '10%',border: '2px gray solid',borderRadius: '25px',display: 'flex',justifyContent: 'space-around'
        }}
      >
        <h1
          style = {{
            lineHeight: '10vh', margin: 0
          }}
        >
          covid75
        </h1>
      </header>

      <div
        style = {{
          backgroundColor: '#fdfdfd',margin: '10%'
        }}
      >
        <h3>Заболевших:</h3>
        <h5>{localData.infected}</h5>
      </div>
    </div>
  );
}

export default App;
