import React, { useState, useEffect } from 'react'

const Header = () => {

	const [logoFontSize, setLogoFontSize] = useState ()

	// HANDLE SIZES
	useEffect (() => {
		handleSize ()
	}, [])

	const handleSize = () => {
		// calc logoFSize
		const cLogoFontSize = document.querySelector('header').clientHeight / 2.166
		setLogoFontSize (cLogoFontSize+'px')
		console.log (cLogoFontSize)
	}

	return (
		<header
			style = {{height: '8.135vh', width: '81.86%', margin: '0 auto', top: '2.50vh', backgroundColor: '#e0e0e0', position: 'relative',
				boxShadow: '3px 3px 10px 0 rgba(0, 0, 0, 0.2), -3px -3px 10px 0 rgba(255, 255, 255, 0.8)', borderRadius: '20px'
			}}
		>
			<div
				style  = {{height: '4vh', margin: 'auto 0'}}
			>
				<h1 style = {{fontFamily: 'Poppins, Roboto', fontSize: 'regular', padding: 0, margin: 0, fontSize: logoFontSize}}>Covid75</h1>
			</div>
			<span> </span>
		</header>
	)
}

export default Header
