import React, {useState, useEffect} from 'react'

const Global = (prop) => {	

	const [lessCountry, setLessCountry] = useState ([])
	console.log (lessCountry)
	//BACK END STUFF
	useEffect (() => backEnd(), [])

	const backEnd = () => {
		const requestOptions = {
			method: 'GET',
			redirect: 'follow'
		}
		fetch('https://api.covid19api.com/summary', requestOptions)
			.then (response => response.json())
			.then (result => {
				resulting (result)
			})
			.catch(error => console.error (error))
	}

	const resulting = (data) => {
		// calculating tops
		// calc less
		calcLess (data.Countries)
	}

	const calcLess = (countries) => {
		let numArray = []
		countries.map ((item, index) => {
			let num = item.TotalConfirmed
			if (numArray.indexOf(num) === -1 | index === 0) numArray.push (num)
		} )
		let minNum = Math.min (...numArray)
		let winIndex = numArray.indexOf (minNum)
		let winCountry
		countries.map ((item, index) => {
			if (index === winIndex) winCountry = item
		})
		setLessCountry (winCountry)
	}

	//VARS
	let mainTrans = '-100'
	if (prop.isOpen) mainTrans = '0'

	return (
		<div style = {{ transform: `translateY(${mainTrans}%)`, transition: 'transform 0.4s ease',
			height:'30vh', width: '100%', backgroundColor:'purple'}}>

		</div>
	)
}

export default Global
